UsageExternalURIEntities.shape_template:
ex:UsageExternalURIShape
a sh:NodeShape ;
sh:targetObjectsOf INTERLINKING_PROPERTY ;
sh:pattern "^(?!DATASET_URI)" .

UsageHashURIsEntities.shape_template:
ex:UsageHashURIsShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:nodeKind sh:IRI ; 
sh:pattern "^[ ^#]*$" .

LabelForEntities.shape_template:
ex:LabelForEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:property [
sh:path LABELING_PROPERTY ;
sh:minCount 1 ;
] .

URIRegexComplianceEntities.shape_template:
ex:URIRegexComplianceEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:pattern "URI_REGEX_PATTERN".

URIsParametersEntities.shape_template:
ex:URIsParametersEntitiesShape
a sh:NodeShape ;
sh:targetClass rdfs:Class ;
sh:not[
sh:pattern "\\?|=" ;
] .

URIsLengthEntities.shape_template:
ex:URIsLengthEntitiesShape
a sh:NodeShape ;
sh:targetClass rdfs:Class ;
sh:maxLength LENGTH_VALUE .

ProlixRDFFeatures.shape_template:
ex:ProlixRDFFeaturesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:not[
sh:or(
[ sh:class rdf:Statement ];
[ sh:class rdf:List ];
[ sh:class rdf:Seq ];
[ sh:class rdf:Bag ];
[ sh:class rdf:Alt ];
)
] .

DifferentLanguagesLabelsEntities.shape_template:
ex:DifferentLanguagesLabelsEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:property [
sh:path LABELING_PROPERTY ;
sh:datatype rdf:langString;
].

DifferentLanguagesDescriptionsEntities.shape_template:
ex:DifferentLanguagesDescriptionsEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:property [
sh:path rdfs:comment ;
sh:datatype rdf:langString;
].

SelfDescriptiveFormat.shape_template:
ex:SelfDescriptiveFormatEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:nodeKind sh:IRI .

BlankNodesUsageEntities.shape_template:
ex:BlankNodesUsageEntitiesShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:not [ sh:nodeKind sh:BlankNode ]

InterlinkingCompleteness.shape_template:
ex:InterlinkingCompletenessShape
a sh:NodeShape ;
sh:targetSubjectsOf TYPE_PROPERTY ;
sh:property [
    sh:path INTERLINKING_PROPERTY ;
    sh:minCount 1 ;
] .
