UsageHashURIsEntities.shape_template:
ex:UsageHashURIsShape
	a sh:NodeShape ;
	sh:targetSubjectsOf TYPE_PROPERTY ;
	sh:or (
	[
		sh:path rdf:type ;
		sh:hasValue rdfs:Class ;
	]
	[
		sh:path rdf:type ;
		sh:hasValue rdf:Property ;
	]
	[
		sh:pattern "^[^#]*$" ;
	]
	) .

