ProlixFeatures.shape_template:
ex:ProlixRDFFeaturesShape
	a sh:NodeShape ;
	sh:targetSubjectsOf rdf:type ;
	sh:or (
	[
		sh:path rdf:type ;
		sh:hasValue rdfs:Class ;
	]
	[
		sh:path rdf:type ;
		sh:hasValue rdf:Property ;
	]
	[
		sh:not [
			sh:or (
				[ sh:class rdf:Statement ] ;
				[ sh:class rdf:List ] ;
				[ sh:class rdf:Seq ] ;
				[ sh:class rdf:Bag ] ;
				[ sh:class rdf:Alt ] ;
			)
		]
	]
	) .

