UsageHashURIsClasses.shape_template:
ex:UsageHashURIsClassesShape
a sh:NodeShape ;
sh:targetClass rdfs:Class ;
sh:or (
[ sh:pattern "^[ ^#]*$"; ]
[ sh:pattern "rdf|owl|dc|XMLSchema|w3"; ]
)

URIRegexComplianceClasses.shape_template:
ex:URIRegexComplianceShape
a sh:NodeShape ;
sh:targetClass rdfs:Class ;
sh:or (
[ sh:pattern "rdf|owl|dc|XMLSchema|w3" ]
[ sh:pattern "URI_REGEX_PATTERN" ; ]
)

UsageHashURIsProperties.shape_template:
ex:UsageHashURIsPropertiesShape
a sh:NodeShape ;
sh:targetClass rdf:Property ;
sh:or (
[ sh:pattern "^#"; ]
[ sh:pattern "rdf|owl|dc|XMLSchema|w3"; ]
).

URIRegexComplianceProperties.shape_template:
ex:URIRegexCompliancePropertiesShape
a sh:NodeShape ;
sh:entailment <https://www.w3.org/ns/entailment/RDFS>;
sh:targetClass rdf:Property ;
sh:or (
[ sh:pattern "rdf|owl|dc|XMLSchema|w3" ]
[ sh:pattern "URI_REGEX_PATTERN" ; ]
).

URIsLengthClass.shape_template:
ex:URIsLengthClassShape
a sh:NodeShape ;
sh:targetClass rdfs:Class ;
sh:maxLength LENGTH_VALUE.

URIsParametersClass.shape_template:
