sh:pattern "\\?|=" ;
]

URIsParametersProperties.shape_template:
ex:URIsParametersPropertiesShape
a sh:NodeShape ;
sh:targetClass rdf:Property ;
sh:not[
sh:pattern "\\?|=" ;
]

URIsLengthProperties.shape_template:
ex:URIsLengthPropertiesShape
a sh:NodeShape ;
sh:targetClass rdf:Property ;
sh:maxLength LENGTH_VALUE.
