AuthenticityOfDataset.shape_template:
ex:AuthenticityOfDatasetShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:and (
	[
		sh:or (
			[
				sh:path dcterms:contributor ;
				sh:minCount 1 ;
			]
			[
				sh:path dcterms:creator ;
				sh:minCount 1 ;
			]
			[
				sh:path dcterms:publisher ;
				sh:minCount 1 ;
			]
		)
	]
	[
		sh:or (
			[
				sh:path dcterms:source ;
				sh:minCount 1 ;
			]
			[
				sh:path dcterms:provenance ;
				sh:minCount 1 ;
			]
		)
	]
)

PresenceMetadata.shape_template:
ex:DatasetMetadataShape
	a sh:NodeShape ;
	sh:targetClass void:Dataset ;
	sh:property [
		sh:path dcterms:title ;
		sh:minCount 1 ;
		sh:or (
			[
				sh:datatype xsd:string;
			]
			[
				sh:datatype rdf:langString;
			]
		);
	];
	sh:property [
		sh:path dcterms:description ;
		sh:minCount 1 ;
		sh:or (
			[
				sh:datatype xsd:string;
			]
			[
				sh:datatype rdf:langString;
			]
		);
	];
	sh:property [
		sh:path foaf:homepage ;
		sh:minCount 1 ;
		sh:nodeKind sh:IRI;
	].

URISpacePressence.shape_template:
ex:URISpacePressenceShape
	a sh:NodeShape;
	sh:targetClass void:Dataset;
	sh:property [
		sh:path void:uriSpace ;
		sh:minCount 1 ;
		sh:datatype xsd:string ;
		sh:message "URISpacePressence - The dataset should specify a URI namespace used for the entities";
	].
