AvailabilityDump.shape_template:
ex:AvailabilityDumpShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:or (
[
sh:path void:dataDump ;
sh:minCount 1 ;
]
[
sh:path (dcat:distribution dcat:downloadURL) ;
sh:minCount 1 ;
]
) .

MachineReadableLicense.shape_template:
ex:MachineReadableShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property [
sh:path dcterms:license ;
sh:minCount 1 ;
] .

AuthenticityDataset.shape_template:
ex:AuthenticityOfDatasetShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:and (
[
sh:or (
[
sh:path dcterms:contributor ;
sh:minCount 1 ;
]
[
sh:path dcterms:creator ;
sh:minCount 1 ;
]
[
sh:path dcterms:publisher ;
sh:minCount 1 ;
]
)
]
[
sh:or (
[
sh:path dcterms:source ;
sh:minCount 1 ;
]
[
sh:path dcterms:provenance ;
sh:minCount 1 ;
]
)
]
)

PresenceMetadata.shape_template:
ex:DatasetMetadataShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property [
sh:path dcterms:title ;
sh:minCount 1 ;
sh:datatype xsd:string;
];
sh:property [
sh:path dcterms:description ;
sh:minCount 1 ;
sh:datatype xsd:string;
];
sh:property [
sh:path foaf:homepage ;
sh:minCount 1 ;
sh:class sh:IRI;
].

ExemplaryResources.shape_template:
ex:ExemplaryResourceShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property [
sh:path void:exampleResource ;
sh:minCount 1 ;
sh:nodeKind sh:IRI ;
]

URIRegexPresence.shape_template:
ex:URIRegexPressenceShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property [
sh:path void:uriRegexPattern ;
sh:minCount 1 ;
sh:datatype xsd:string ;
sh:pattern "^http://|^https://";
].

VocabularyExistence.shape_template:
ex:VocabularyExistenceShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property[
sh:path void:vocabulary ;
sh:nodeKind sh:IRI ;
sh:minCount 1 ;
]

SerializationFormats.shape_template:
ex:SerializationFormatsShape
a sh:NodeShape ;
sh:targetClass void:Dataset ;
sh:property [
sh:path void:feature;
sh:minCount 1;
sh:maxCount 5;
sh:nodeKind sh:IRI;
sh:in (
<http://www.w3.org/ns/formats/N3>
<http://www.w3.org/ns/formats/N-Triples>
<http://www.w3.org/ns/formats/RDF_XML>
<http://www.w3.org/ns/formats/RDFa>
<http://www.w3.org/ns/formats/Turtle>
);
].
